    val listData: ArrayList<Food>
        get(){
            val list = arrayListOf<Food>()
            for(posisi in foodNames.indices){
                val food = Food()
                food.name = foodNames[posisi]
                food.detail = foodDetails[posisi]
                food.photo = foodImages[posisi]
                food.price = foodPrices[posisi]
                list.add(food)

